package Project;

public class nestedswitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int tech = 2; 
	    int course = 2;  
	      
	    switch(tech){ 
	    case 1: 
	          System.out.println("java"); 
	          break; 
	    case 2: System.out.println("Python");
	    case 3:  
	          switch(course){ 
	          case 1: 
	                 System.out.println("eee"); 
	                 break; 
	          case 2: 
	                System.out.println("ece"); 
	}

}
	}
}
